import React, { useRef, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  html, body {
    height: 100%;
    font-family: 'Roboto', sans-serif;
  }

  body {
    background-color: #f0f4f8;
  }
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  padding: 20px;
  background: linear-gradient(135deg, #4b79a1 0%, #283e51 100%);
`;

const Title = styled.h2`
  font-size: 2.5rem;
  color: #ffffff;
  margin-bottom: 20px;
  text-shadow: 2px 2px 5px rgba(0, 0, 0, 0.3);
`;

const VideoContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: 20px;
`;

const Video = styled.video`
  width: 600px;
  height: 400px;
  border-radius: 10px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.5);

  @media (max-width: 768px) {
    width: 100%;
    height: auto;
  }
`;

const Canvas = styled.canvas`
  display: ${({ hasPhoto }) => (hasPhoto ? 'block' : 'none')};
  margin-top: 20px;
  border-radius: 10px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.5);
`;

const ButtonSection = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 20px;
  gap: 20px;
`;

const ButtonGroup = styled.div`
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 15px;
`;

const Button = styled.button`
  padding: 12px 20px;
  font-size: 1.1rem;
  color: white;
  background: ${({ danger }) => (danger ? '#e74c3c' : '#3498db')};
  border: none;
  border-radius: 30px;
  cursor: pointer;
  box-shadow: 0px 8px 15px rgba(0, 0, 0, 0.3);
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0px 12px 20px rgba(0, 0, 0, 0.4);
  }

  &:focus {
    outline: none;
  }

  &:disabled {
    background: #95a5a6;
    cursor: not-allowed;
  }
`;

const Camera = () => {
  const navigate = useNavigate();
  const videoRef = useRef(null);
  const photoRef = useRef(null);
  const [hasPhoto, setHasPhoto] = useState(false);
  const [recording, setRecording] = useState(false);
  const mediaRecorder = useRef(null);
  const [recordedChunks, setRecordedChunks] = useState([]);

  useEffect(() => {
    // Automatically start the camera when the component mounts
    startCamera();

    // Clean up the media stream when the component unmounts
    return () => {
      const stream = videoRef.current?.srcObject;
      if (stream) {
        const tracks = stream.getTracks();
        tracks.forEach((track) => track.stop());
      }
    };
  }, []);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      videoRef.current.srcObject = stream;
      videoRef.current.play();
    } catch (error) {
      console.error('Error accessing camera and microphone:', error);
      // Handle the error, e.g., show a message to the user
    }
  };

  const takePhoto = () => {
    const width = 400;
    const height = 300;
    const photo = photoRef.current;
    const context = photo.getContext('2d');

    photo.width = width;
    photo.height = height;
    context.drawImage(videoRef.current, 0, 0, width, height);
    setHasPhoto(true);
  };

  const startRecording = () => {
    const stream = videoRef.current.srcObject;
    mediaRecorder.current = new MediaRecorder(stream);
    mediaRecorder.current.ondataavailable = handleDataAvailable;
    mediaRecorder.current.start();
    setRecording(true);
  };

  const stopRecording = () => {
    mediaRecorder.current.stop();
    setRecording(false);
  };

  const handleDataAvailable = (event) => {
    if (event.data.size > 0) {
      setRecordedChunks((prev) => [...prev, event.data]);
    }
  };

  const saveVideo = () => {
    const blob = new Blob(recordedChunks, { type: 'video/webm' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'recorded_video.webm';
    document.body.appendChild(a);
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <>
      <GlobalStyle />
      <Container>
        <Title>Camera</Title>

        <VideoContainer>
          <Video ref={videoRef} />
        </VideoContainer>

        <ButtonSection>
          <ButtonGroup>
            <Button onClick={takePhoto}>Capture Photo</Button>
            {hasPhoto && (
              <>
                <Canvas ref={photoRef} hasPhoto={hasPhoto} />
                <Button onClick={() => setHasPhoto(false)}>Clear Photo</Button>
              </>
            )}
          </ButtonGroup>

          <ButtonGroup>
            <Button onClick={startRecording} disabled={recording}>
              Start Recording
            </Button>
            <Button onClick={stopRecording} disabled={!recording} danger>
              Stop Recording
            </Button>
            {recordedChunks.length > 0 && <Button onClick={saveVideo}>Save Video</Button>}
          </ButtonGroup>

          <Button onClick={() => navigate('/')}>Go to Home</Button>
        </ButtonSection>
      </Container>
    </>
  );
};

export default Camera;
