import React, { useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom'; 
import styled, { createGlobalStyle } from 'styled-components';
import { FaCamera, FaStop, FaVideo, FaHome, FaPhotoVideo } from 'react-icons/fa'; 
import { Tooltip } from 'react-tooltip'; 

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  html, body {
    height: 100%;
    font-family: 'Roboto', sans-serif;
  }

  body {
    background-color: #f0f4f8;
  }
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  padding: 20px;
  background: linear-gradient(135deg, #4b79a1 0%, #283e51 100%);
`;

const Title = styled.h2`
  font-size: 2.5rem;
  color: #ffffff;
  margin-bottom: 20px;
  text-shadow: 2px 2px 5px rgba(0, 0, 0, 0.3);
`;

const VideoContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: 20px;
`;

const Video = styled.video`
  width: 600px;
  height: 400px;
  border-radius: 10px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.5);

  @media (max-width: 768px) {
    width: 100%;
    height: auto;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 15px;
  margin-top: 20px;
`;

const IconButton = styled.button`
  padding: 10px;
  font-size: 2rem;
  color: white;
  background: transparent;
  border: none;
  cursor: pointer;
  transition: transform 0.3s ease;
  position: relative;

  &:hover {
    transform: translateY(-3px);
  }

  &:focus {
    outline: none;
  }

  &:disabled {
    color: #95a5a6;
    cursor: not-allowed;
  }
`;

const HomeButton = styled(IconButton)`
  background-color: #3498db;
  border-radius: 50%;
  box-shadow: 0px 8px 15px rgba(0, 0, 0, 0.3);
`;

const Camera = () => {
  const navigate = useNavigate();
  const videoRef = useRef(null);
  const mediaRecorder = useRef(null);
  const [recording, setRecording] = useState(false);
  const [photo, setPhoto] = useState(null);
  const [cameraStarted, setCameraStarted] = useState(false);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      videoRef.current.srcObject = stream;
      videoRef.current.play();
      setCameraStarted(true);
    } catch (err) {
      console.error('Error accessing camera: ', err);
    }
  };

  const stopCamera = () => {
    if (videoRef.current.srcObject) {
      const stream = videoRef.current.srcObject;
      const tracks = stream.getTracks();
      tracks.forEach((track) => track.stop());
      setCameraStarted(false);
    }
  };

  const startRecording = () => {
    if (cameraStarted) {
      const stream = videoRef.current.srcObject;
      mediaRecorder.current = new MediaRecorder(stream);
      mediaRecorder.current.start();
      setRecording(true);
    }
  };

  const stopRecording = () => {
    if (mediaRecorder.current) {
      mediaRecorder.current.stop();
      setRecording(false);
    }
  };

  const capturePhoto = () => {
    if (cameraStarted) {
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');
      canvas.width = videoRef.current.videoWidth;
      canvas.height = videoRef.current.videoHeight;
      context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
      setPhoto(canvas.toDataURL('image/png'));
    }
  };

  return (
    <>
      <GlobalStyle />
      <Container>
        <Title>Camera</Title>

        <VideoContainer>
          <Video ref={videoRef} />
          <ButtonGroup>
            {!cameraStarted ? (
              <Tooltip content="Start Camera">
                <IconButton onClick={startCamera}>
                  <FaCamera />
                </IconButton>
              </Tooltip>
            ) : (
              <Tooltip content="Stop Camera">
                <IconButton onClick={stopCamera}>
                  <FaStop />
                </IconButton>
              </Tooltip>
            )}

            {cameraStarted && (
              <Tooltip content="Capture Photo">
                <IconButton onClick={capturePhoto}>
                  <FaCamera />
                </IconButton>
              </Tooltip>
            )}
          </ButtonGroup>

          {photo && (
            <img src={photo} alt="Captured" style={{ marginTop: '20px', borderRadius: '10px', boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.5)' }} />
          )}
        </VideoContainer>

        <ButtonGroup>
          <Tooltip content="Start Recording">
            <IconButton onClick={startRecording} disabled={recording || !cameraStarted}>
              <FaVideo />
            </IconButton>
          </Tooltip>

          <Tooltip content="Stop Recording">
            <IconButton onClick={stopRecording} disabled={!recording}>
              <FaStop />
            </IconButton>
          </Tooltip>
        </ButtonGroup>

        <ButtonGroup>
          <HomeButton onClick={() => navigate('/')}>
            <FaHome />
          </HomeButton>
        </ButtonGroup>
      </Container>
    </>
  );
};

export default Camera;
