import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: linear-gradient(135deg, #6a11cb 0%, #2575fc 100%);
  color: white;
  text-align: center;
  padding: 0 20px;

  @media (max-width: 768px) {
    height: auto;
    padding: 20px;
  }
`;

const Title = styled.h1`
  font-size: 3rem;
  margin-bottom: 20px;
  font-family: 'Arial', sans-serif;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const Description = styled.p`
  font-size: 1.5rem;
  font-weight: 300;
  line-height: 1.6;
  max-width: 600px;
  font-family: 'Verdana', sans-serif;
  margin-bottom: 30px;

  @media (max-width: 768px) {
    font-size: 1.2rem;
  }
`;

const Button = styled.button`
  padding: 10px 20px;
  font-size: 1rem;
  color: #fff;
  background-color: #ff5722;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #e64a19;
  }
`;

const Home = () => {
  const navigate = useNavigate();

  const handleCameraNavigation = () => {
    navigate('/camera');
  };

  return (
    <HomeContainer>
      <Title>Welcome to the Home Page</Title>
      <Description>This is the starting page of the application, designed to provide an introduction and guide you through the platform.</Description>
      <Button onClick={handleCameraNavigation}>Go to Camera Page</Button>
    </HomeContainer>
  );
};

export default Home;
